# ===========================================================================
#  Source / Destination pairs per grid size
# ===========================================================================

def _src_dst(size: int) -> np.ndarray:
    """Top-left to bottom-right source/destination for any grid size."""
    return np.vstack((np.array([0, 0]), np.array([size - 1, size - 1])))


# ===========================================================================
#  Maze catalogue
# ===========================================================================

MAZES = {
    # (size, difficulty) : (maze_array, src_dst_array)
    (15, 'simple'):   (MAZE_15_SIMPLE,   _src_dst(15)),
    (15, 'moderate'): (MAZE_15_MODERATE, _src_dst(15)),
    (15, 'complex'):  (MAZE_15_COMPLEX,  _src_dst(15)),

    (30, 'simple'):   (MAZE_30_SIMPLE,   _src_dst(30)),
    (30, 'moderate'): (MAZE_30_MODERATE, _src_dst(30)),
    (30, 'complex'):  (MAZE_30_COMPLEX,  _src_dst(30)),

    (60, 'simple'):   (MAZE_60_SIMPLE,   _src_dst(60)),
    (60, 'moderate'): (MAZE_60_MODERATE, _src_dst(60)),
    (60, 'complex'):  (MAZE_60_COMPLEX,  _src_dst(60)),
}


# ===========================================================================
#  Save all mazes
# ===========================================================================

def save_all_mazes(output_dir: Path = GRID_MAP_DIR) -> None:
    """Save every maze and its source/destination to output_dir."""
    output_dir.mkdir(parents=True, exist_ok=True)

    for (size, difficulty), (maze, src_dst) in MAZES.items():
        prefix     = f'{size}x{size}_{difficulty}'
        maze_path  = output_dir / f'{prefix}_maze.npy'
        loc_path   = output_dir / f'{prefix}_src_dst.npy'

        np.save(maze_path, maze)
        np.save(loc_path,  src_dst)

        wall_pct = 100.0 * np.mean(maze)
        print(f'[{size:>2}x{size:<2}] {difficulty:<8}  '
              f'walls={wall_pct:5.1f}%  '
              f'->  {maze_path.name}  |  {loc_path.name}')

    print(f'\nAll {len(MAZES)} mazes saved to: {output_dir.resolve()}')


# ===========================================================================
#  Entry point
# ===========================================================================

if __name__ == '__main__':
    save_all_mazes()
